mapper.select:
SELECT h.apt_seq, h.sgg_cd, h.umd_cd, h.umd_nm, h.jibun, h.road_nm_sgg_cd, h.road_nm, h.road_nm_bonbun, h.road_nm_bubun, h.apt_nm, h.build_year, h.latitude, h.longitude, d.deal_amount as latest_deal_amount, d.deal_year as latest_deal_year, d.deal_month as latest_deal_month, d.deal_day as latest_deal_day, d.floor as latest_deal_floor, d.exclu_use_ar as latest_deal_area FROM houseinfos h LEFT JOIN ( SELECT hd1.apt_seq, hd1.deal_amount, hd1.deal_year, hd1.deal_month, hd1.deal_day, hd1.floor, hd1.exclu_use_ar FROM housedeals hd1 INNER JOIN ( SELECT apt_seq, MAX(no) as max_no FROM housedeals GROUP BY apt_seq ) hd2 ON hd1.apt_seq = hd2.apt_seq AND hd1.no = hd2.max_no ) d ON h.apt_seq = d.apt_seq
AND h.umd_nm LIKE CONCAT('%', #{umdNm}, '%')
AND h.umd_nm IN
#{name}
AND h.apt_nm LIKE CONCAT('%', #{aptName}, '%')
AND d.deal_amount IS NOT NULL AND CAST(REPLACE(d.deal_amount, ',', '') AS UNSIGNED) >= #{minPrice}
AND d.deal_amount IS NOT NULL AND CAST(REPLACE(d.deal_amount, ',', '') AS UNSIGNED) <= #{maxPrice}
AND d.exclu_use_ar IS NOT NULL AND d.exclu_use_ar >= #{minArea}
AND d.exclu_use_ar IS NOT NULL AND d.exclu_use_ar <= #{maxArea}
AND h.sgg_cd = #{sggCd}
ORDER BY h.apt_seq DESC
LIMIT ${limit}

SELECT COUNT(DISTINCT h.apt_seq)
FROM houseinfos h
LEFT JOIN (
    SELECT hd1.apt_seq,
           hd1.deal_amount,
           hd1.exclu_use_ar
    FROM housedeals hd1
    INNER JOIN (
        SELECT apt_seq, MAX(no) as max_no
        FROM housedeals
        GROUP BY apt_seq
    ) hd2 ON hd1.apt_seq = hd2.apt_seq AND hd1.no = hd2.max_no
) d ON h.apt_seq = d.apt_seq


        AND h.umd_nm LIKE CONCAT('%', #{umdNm}, '%')


        AND h.umd_nm IN

            #{name}



        AND h.apt_nm LIKE CONCAT('%', #{aptName}, '%')


        AND d.deal_amount IS NOT NULL
        AND CAST(REPLACE(d.deal_amount, ',', '') AS UNSIGNED) >= #{minPrice}


        AND d.deal_amount IS NOT NULL
        AND CAST(REPLACE(d.deal_amount, ',', '') AS UNSIGNED) <= #{maxPrice}


        AND d.exclu_use_ar IS NOT NULL
        AND d.exclu_use_ar >= #{minArea}


        AND d.exclu_use_ar IS NOT NULL
        AND d.exclu_use_ar <= #{maxArea}


        AND h.sgg_cd = #{sggCd}

SELECT COUNT(*)
FROM property_scraps
WHERE user_id = #{userId} AND apt_seq = #{aptSeq}
